#include<bits/stdc++.h>
using namespace std;
int p[120000],c[120000],c2[120000],a[110000],dp[11000][210],dp2[11000][210];
int k, cnt;

int lowbit(int x){return x&(-x);}
void add(int x)
{
    while(x<=k)
	{
        c[x]++;
        x+=lowbit(x);
    }
}
void add2(int x)
{
    while(x<=k)
	{
        c2[x]++;
        x+=lowbit(x);
    }
}
int getsum(int x)
{
    int sum=0;
    while(x>0)
	{
        sum+=c[x];
        x-=lowbit(x);
    }
    return sum;
}
int getsum2(int x)
{
    int sum=0;
    while(x>0)
	{
        sum+=c2[x];
        x-=lowbit(x);
    }
    return sum;
}
int main()
{
    int n;scanf("%d%d",&n, &k);
    for(int i=1;i<=n;i++)
	{
		scanf("%d",&a[i]);
		if(a[i]>0) add(a[i]);
		else p[i]=++cnt,dp[cnt][0]=10000000009;//确保在-1的时候进行dp 
	}
	long long ans=0;
    for (int i=1;i<=n;i++)
    {
    	if(a[i]>0)
    	{
    		ans+=getsum2(k)-getsum2(a[i]);//算逆序对 
    		add2(a[i]);
		}	
		else
		{
			for (int j=1;j<=k;j++)
			{
				dp[p[i]][j]=min(dp[p[i]][j-1],dp[p[i]-1][j]+getsum2(k)-getsum2(j)+getsum(j-1)-getsum2(j-1));
			}
		}
	}
   	ans+=dp[cnt][k];
   	printf("%lld",ans);
    return 0;
}
