上代码，具体看注解
```
#include<iostream>
#include<cstdio>
#include<cstring>
#include<cmath>
#define fu(a,b,c) for(int a=b;a<=c;a++)
#define fd(a,b,c) for(int a=b;a>=c;a--)
using namespace std;
const int INF=0x3f3f3f3f,N=500050;
int ver[N*2],head[N],Next[N*2],size[N],top[N],fa[N],d[N];
int n,t,tot;
int a,b,ans=INF,k,x,y,z;
void add(int x,int y)
{
	ver[++tot]=y,Next[tot]=head[x],head[x]=tot;
}
void dfs(int x)
{
	size[x]=1,d[x]=d[fa[x]]+1;
	for(int i=head[x];i;i=Next[i])
	{
		int y=ver[i];
		if(y!=fa[x])
		fa[y]=x,dfs(y),size[x]+=size[y];
	}
}
void dfs_(int x)
{
	if(!top[x]) top[x]=x;
	int t=0;
	for(int i=head[x];i;i=Next[i])
	{
		int y=ver[i];
		if(y!=fa[x]&&size[y]>size[t])
		t=y;
	}
	if(t) top[t]=top[x],dfs_(t);
	for(int i=head[x];i;i=Next[i])
	{
		int y=ver[i];
		if(y!=fa[x]&&y!=t)
		dfs_(y);
	}
}
int lca(int x,int y)
{
	while(top[x]!=top[y])
	{
		if(d[top[x]]<d[top[y]]) swap(x,y);
		x=fa[top[x]];
	}
	if(d[x]>d[y]) swap(x,y);
	return x;
}
void check()
{
	int s=d[x]+d[y]+d[z]-d[a]-2*d[b];
    //此式子不变
	if(s<ans) ans=s,k=a;
}
void answer()
{
	scanf("%d%d%d",&x,&y,&z);
	ans=INF;
    //枚举a为三个lca，并检查，更新答案
	a=lca(x,y),b=lca(a,z);
	check();
	a=lca(x,z),b=lca(a,y);
	check();
	a=lca(y,z),b=lca(a,x);
	check();
	printf("%d %d\n",k,ans);
	return;
}
int main()
{
	scanf("%d%d",&n,&t);
	fu(i,1,n-1)
	{
		int x,y;
		scanf("%d%d",&x,&y);
		add(x,y),add(y,x);
	}
	dfs(1);
	dfs_(1);
	while(t--) answer();
	return 0;
}
