O2优化代码
```cpp
#include<cstdio>
#include<cstring>
#include<algorithm>
using namespace std;
#define maxn 505555
int n,m;
struct node
{
    int to;
    int nxt;
}nd[2*maxn];
int head[maxn],dfn[maxn],dep[maxn],cnt,tot;
//dfn:每个节点在欧拉序中第一次出现的位置
int f[2*maxn][21];
int lg[2*maxn];
void init()
{
    memset(head,-1,sizeof(head));
    lg[1]=0;
    for(int i=2;i<=2*n-1;i++)
    {
        lg[i]=lg[i>>1]+1;
    }
    cnt=1;
    return;
}
void add(int x,int y)
{
    nd[cnt].to=y;
    nd[cnt].nxt=head[x];
    head[x]=cnt++;
    return;
}
void dfs(int rt,int x)
{
    tot++;
    dfn[x]=tot;
    f[tot][0]=x;
    for(int i=head[x];i!=-1;i=nd[i].nxt)
    {
        int t=nd[i].to;
        if(t!=rt)
        {
            dep[t]=dep[x]+1;
            dfs(x,t);
            tot++;
            f[tot][0]=x;
        }
    }
    return;
}
void rmq()
{
    for(int i=1;i<=lg[tot];i++)
    {
        for(int x=1;(x+(1<<i)-1)<=tot;x++)
        {
            int r=x+(1<<(i-1));
            f[x][i]=dep[f[x][i-1]]<dep[f[r][i-1]]?f[x][i-1]:f[r][i-1];
        }
    }
    return;
}
int lca(int x,int y)
{
    if(x>y)
    {
        swap(x,y);
    }
    int k=lg[y-x+1];
    return dep[f[x][k]]<dep[f[y-(1<<k)+1][k]]?f[x][k]:f[y-(1<<k)+1][k];
}
int dist(int a,int b)
{
    return dep[a]+dep[b]-2*dep[lca(dfn[a],dfn[b])];
}
int main()
{
    scanf("%d%d",&n,&m);
    init();
    for(int i=1;i<n;i++)
    {
        int a,b;
        scanf("%d%d",&a,&b);
        add(a,b);
        add(b,a);
    }
    dfs(0,1);
    rmq();
    while(m--)
    {
        int a,b,c;
        scanf("%d%d%d",&a,&b,&c);
        int d=lca(dfn[a],dfn[b]);
        int e=lca(dfn[a],dfn[c]);
        int g=lca(dfn[b],dfn[c]);
        if(d==e)
        {
        	printf("%d %d\n",g,dist(b,c)+dist(g,a));
        }else if(d==g)
        {
        	printf("%d %d\n",e,dist(a,c)+dist(e,b));
        }else
        {
        	printf("%d %d\n",d,dist(a,b)+dist(d,c));
        }
    }
    return 0;
}
