#include<iostream>
#include<cstdio>
#include<algorithm>
#include<cmath>
#include<cstring>
using namespace std;
int n,m,head[500007],cent,dep[500007],fa[500007][30],len[500007];
struct node{
	int next,to,w;
}edge[1000007];

void add(int u,int v,int w){
	edge[++cent]=(node){head[u],v,w};head[u]=cent;
}

void dfs(int x,int dy){
	dep[x]=dy;
	for(int i=head[x];i;i=edge[i].next){
		int y=edge[i].to;
		if(y==fa[x][0]) continue;
		fa[y][0]=x;
		len[y]=len[x]+edge[i].w;
		dfs(y,dy+1);
	}
	return ;
}

void Init(){
	fa[1][0]=-1;
	dfs(1,0);
	for(int i=1;1<<i<n;i++){
		for(int j=1;j<=n;j++){
			if(fa[j][i-1]<0) fa[j][i]=-1;
			else fa[j][i]=fa[fa[j][i-1]][i-1];
		}
	}
	return ;
}

int lca(int x,int y){
	if(dep[x]<dep[y]) swap(x,y);
	for(int i=0,d=dep[x]-dep[y];d;d>>=1,i++){
		if(d&1) x=fa[x][i];
	}
	if(x==y) return x;
	for(int i=log(n)+1;i>=0;i--){
		if(fa[x][i]!=fa[y][i]){
			x=fa[x][i];
			y=fa[y][i];
		}
	}
	return fa[x][0];
}

void work(int a,int b,int c){
	int x=lca(a,b),y=lca(b,c),z=lca(a,c),exit;
	if(x==y) exit=z;
	else if(y==z) exit=x;
	else if(x==z) exit=y;
	printf("%d %d\n",exit,dep[a]+dep[b]+dep[c]-dep[x]-dep[y]-dep[z]);
}

int main(){
	scanf("%d%d",&n,&m);
	for(int i=1,a,b;i<=n-1;i++){
		scanf("%d%d",&a,&b);
		add(a,b,1),add(b,a,1);
	}
	Init();
	for(int i=1,a,b,c;i<=m;i++){
		scanf("%d%d%d",&a,&b,&c);
		work(a,b,c);
	}
	return 0;
}
