}

inline int get(int x){
	if(fa[x]==x) return x;
	return fa[x]=get(fa[x]);
}

inline void Tarjan(int u){
	vis[u]=1;
	for(register int i=head[u];i;i=edge[i].next){
		int v=edge[i].to;
		if(vis[v]) continue;
		dep[v]=dep[u]+edge[i].w;
		Tarjan(v);
		fa[v]=u;
