		}else if(b==c) {
			printf("%d %d\n",a,dep[x]+dep[y]+dep[z]-dep[a]-dep[b]-dep[c]);
		}else if(a==c) {
			printf("%d %d\n",b,dep[x]+dep[y]+dep[z]-dep[a]-dep[b]-dep[c]);
		}

	}
}

int main(){
	scan(n),scan(m);
	for(int i=1;i<=n;i++) fa[i]=i;
	for(int i=1,a,b;i<=n-1;i++){
