	for(register int i=1;i<=3*m;i+=3){
		int a,b,c;
		scan(a),scan(b),scan(c);
		see[i][0]=a,see[i][1]=b,see[i][2]=c;
		add1(a,b,i),add1(b,a,i),add1(a,c,i+1);
		add1(c,a,i+1),add1(c,b,i+2),add1(b,c,i+2);
	}
