void ins(int x,int y)
{
	len++;a[len].x=x;a[len].y=y;
	a[len].next=last[x];last[x]=len;
}
void dfs(int x,int fa)
{
	t[x].dep=t[fa].dep+1;
	t[x].par[0]=fa;
	for(int i=1;(1<<i)<=t[x].dep;i++)
		t[x].par[i]=t[t[x].par[i-1]].par[i-1];
