		}
	}
}
int lca(int x,int y)
{
	if(t[x].dep<t[y].dep) swap(x,y);
	for(int i=18;i>=0;i--)
		if(t[x].dep-t[y].dep>=(1<<i))
			x=t[x].par[i];
	if(x==y) return x;
	for(int i=18;i>=0;i--)
	{
		if(t[x].dep>=(1<<i) && t[x].par[i]!=t[y].par[i])
		{
			x=t[x].par[i];
			y=t[y].par[i];
		}
	}
	return t[x].par[0];
}
int main()
{
