#define int long long
using namespace std;
const int N = 101000;
int x[N], a[N], b[N], c[N];
signed main()
{
  int n;
  cin >> n;
  for (int i = 1; i <= n; i++)  
    cin >> x[i];
  for (int i = 1; i <= n; i++)  
    cin >> a[i];
  char o;
  cin >> o;
  for (int i = 1; i <= n; i++)  
    cin >> b[i];
  if (o == '+')
  {
    for (int i = 1; i <= n; i++)
      c[i] = a[i] + b[i];
    for (int i = n; i; i--)
      c[i - 1] += c[i] / x[i], c[i] %= x[i];
    for (int i = 1; i <= n; i++)
      cout << c[i] << ' ';
    cout << '\n';
  }
  else
  {
    for (int i = 1; i <= n; i++)
      c[i] = a[i] - b[i];
    for (int i = n; i; i--)
      if (c[i] < 0)
      {
        c[i] += x[i];
        c[i - 1]--;
      }
    for (int i = 1; i <= n; i++)
      cout << c[i] << ' ';
    cout << '\n';
  }
  return 0;
