  for(int i = 1; i <= 4; ++i)
     for(int j = 1; j <= 4; ++j)
        a[i][j] = read();
  for(int i = 1; i <= 4; ++i) //得到两个队列
     for(int j = 1; j <= 4; ++j){
        b[i][j] = read();
        if(a[i][j] != b[i][j])
          if(a[i][j] == 1)
            needmove[++nm] = i * 10 + j;
          else
            needgot[++ng] = i * 10 + j;
     }
  for(int i = 1; i <= nm; ++i)  //两两匹配记录所有可能的操作
     for(int j = 1; j <= ng; ++j)
        e[++num].from = needmove[i], e[num].to = needgot[j], 
        e[num].dis = abs(needmove[i] / 10 - needgot[j] / 10) + abs(needmove[i] % 10 - needgot[j] % 10);
  dfs(0, 0, 1);
  printf("%d\n", ans);
