int vis[2000032];

queue<int>q;

void bfs(int zt)
{
	q.push(zt);
	while(!q.empty())
	{
		int u=q.front();
		q.pop();
		if(dp[u]>ans)
		return ;
		if(u==js)
		{ 
			ans=min(dp[u],ans);
			return ;
		}
		for(int i=0;i<=15;i++)
		{
			if((1<<i)&u)
			{
				if(((1<<(i+1))&u)==0&&i!=15&&i!=3&&i!=7&&i!=11)
				{
					int v=u-(1<<i)+(1<<(i+1));
					dp[v]=min(dp[v],dp[u]+1);
					if(!vis[v])
					q.push(v);
					if(v==js)
					{
						ans=min(dp[v],ans);
					}

