				if(((1<<(i-1)&u))==0&&i!=0&&i!=4&&i!=8&&i!=12)
				{
					int v=u-(1<<i)+(1<<(i-1));
					dp[v]=min(dp[v],dp[u]+1);
					if(!vis[v])
					q.push(v); 
					vis[v]=1;
					if(v==js)
					{
						ans=min(dp[v],ans);
					}

				}	
				if(i!=0&&i!=1&&i!=2&&i!=3&&((1<<(i-4))&u)==0)
				{
					int v=u-(1<<i)+(1<<(i-4));
					dp[v]=min(dp[v],dp[u]+1); 
					if(!vis[v])
					q.push(v);
					vis[v]=1;
					if(v==js)
					{
						ans=min(dp[v],ans);
					}
				}
				if(i!=15&&i!=14&&i!=13&&i!=12&&((1<<(i+4))&u)==0)
				{
					int v=u-(1<<i)+(1<<(i+4));
					dp[v]=min(dp[v],dp[u]+1); 
					if(!vis[v])
					q.push(v);
					vis[v]=1;
					if(v==js)
					{
						ans=min(dp[v],ans);
					}
				}	
			}
		}
	}


}

