- 状压题解
- 先将目标和初始的状态压缩
- 然后从初始状态开始搜索，每次变了一个状态就更新这个状态需要的步数，直到搜到目标状态
```cpp
#include<iostream>
#include<cstdio>
#include<queue>
#include<cstring>
using namespace std;
queue<int>q;
int goal,start,dis[1000001];
char s[10];
int main()
{
	memset(dis,0x3f3f3f3f,sizeof(dis));//初始化 
	for(int i=1;i<=4;i++)
	{
        scanf("%s",s+1);
        for(int j=1;j<=4;j++)
		{
            start|=(s[j]-'0')*(1<<((i-1)*4+j));//状态压缩，将原来的状态压缩 
        }
    }
    for(int i=1;i<=4;i++)
	{
        scanf("%s",s+1);
        for(int j=1;j<=4;j++)
		{
            goal|=(s[j]-'0')*(1<<((i-1)*4+j));//状态压缩，将目标的状态压缩 
        }
    }
    dis[start]=0;
	q.push(start);
	while(!q.empty())
	{
		int x=q.front();
		q.pop();
		if(x==goal)//找到目标 
		{
			break;
		}
		for(int i=1;i<=16;++i)
		{
			if(x&(1<<i))
			{
			    if(i+4<=16&&!(x&(1<<(i+4)))&&dis[(x^(1<<i))|1<<(i+4)]>dis[x]+1)//向下搜 
				{
                    dis[(x^(1<<i))|1<<(i+4)]=dis[x]+1;
                    q.push((x^(1<<i))|1<<(i+4));
                }
                if(i-4>=1&&!(x&(1<<(i-4)))&&dis[(x^(1<<i))|1<<(i-4)]>dis[x]+1)//向上搜 
				{
                    dis[(x^(1<<i))|1<<(i-4)]=dis[x] + 1;
                    q.push((x^(1<<i))|1<<(i - 4));
                }
                if(i%4!=0&&!(x&(1<<(i + 1)))&&dis[(x^(1 << i))|1<<(i+1)]>dis[x]+1)//向右搜 
				{
                    dis[(x^(1<<i))|1<<(i+1)]=dis[x]+1;
                    q.push((x^(1<<i))|1<<(i+1));
                }
                if(i%4!=1&&!(x&(1<<(i-1)))&&dis[(x^(1<<i))|1<<(i-1)]>dis[x]+1)//向左搜 
				{
                    dis[(x^(1<<i))|1<<(i-1)]=dis[x]+1;
                    q.push((x^(1<<i))|1<<(i-1));
                }
			}
		}
	}
	printf("%d",dis[goal]);
	return 0;
}
```

