```

#include <cstdio>
#include <iostream>
#include <algorithm>
using namespace std;
struct kk {
	int x,y;
} c[26] ;//记录每一个a[][]有玩具的地方 
int a[5][5],b[5][5],w[5][5][5][5] ;
bool d[5][5];
int _sum = 0,_min = 999999999 ;//_sum记录 a[][] == 1 的个数, _min 是最小值 （及答案） 
void BFS ( int tot , int ans ) {//tot 是寻找到的个数 , ans 是需要步数 
	if ( tot > _sum ) {  //当搜索个数 > 原本个数时，跳出 
		_min = min ( _min , ans ) ; // 统计答案 
		return ;
	}
	for ( int i = 1 ; i <= 4 ; i ++ ) {
		for ( int j = 1 ; j <= 4 ; j ++ ) {
			if ( w[c[tot].x][c[tot].y][i][j] != 0 && d[i][j] == 0) {
				d[i][j] = 1 ;
				BFS ( tot + 1 , ans + w[c[tot].x][c[tot].y][i][j] ) ; //进入更深一层的递归  
				d[i][j] = 0 ; //回溯 
			}
		}
	}//查找 
}

int main() {
	char ch ;//用字符更好输入 
	for ( int i = 1 ; i <= 4 ; i ++ ) {
		for ( int j = 1 ; j <= 4 ; j ++ ) {
			cin >> ch;
			a[i][j] = ch - '0' ;
		}
	}//输入 
	for ( int i = 1 ; i <= 4 ; i ++ ) {
		for ( int j = 1 ; j <= 4 ; j ++ ) {
			cin >> ch ;
			b[i][j] = ch - '0' ;
			if ( a[i][j] == b[i][j] ) a[i][j] = b[i][j] = 0 ;
		}
	}//输入
	for (int i = 1 ; i <= 4 ; i ++ ) {
		for (int j = 1 ; j <= 4 ; j ++ ) {
			if (a[i][j] == 1) {
				c[++ _sum].x = i;
				c[_sum].y = j ;
				for (int k = 1; k <= 4; k ++) {
					for (int x = 1 ; x <= 4; x ++) {
						if (b[k][x] == 1) w[i][j][k][x] = abs(i - k) + abs(j - x) ;
					}
				}
			}
		}
	}//w[][][][]储存（i,j） -->  (k,x) 的距离 
	BFS(1,0);//BFS搜索 
	printf("%d",_min);//输出 
	return 0 ;
}

蒟蒻逛了一下题解区，发现大佬们都用二进制判重（可是我不会），就发一篇map判重。




```cpp
#include<iostream>
#include<cstdio>
#include<algorithm>
#include<cstring>
#include<map>
using namespace std;
map<long long int,bool> mp;
struct node
{
	int a[7][7];
};
node q[1000000];//stl会更快；
int step[1000000];
int h=0,t=1;
long long int p;
long long int turn(node a)//转化成16位数字，方便用map；
{
	long long int s=0;
	for(int i=1;i<=4;i++)
		for(int j=1;j<=4;j++)
			s=s*10+a.a[i][j];
	return s;
}
int ans;
bool change(int h)
{
	node a=q[h];
	for(int j=1;j<=4;j++)
	{
		for(int i=1;i<=3;i++)
		{
			node tmp=a;
			swap(tmp.a[i][j],tmp.a[i+1][j]);
			long long int y=turn(tmp);
			if(mp[y]==0)
			{
				mp[y]=1;
				t++;
				q[t]=tmp;
				step[t]=step[h]+1;
			}
			if(y==p)
			{
				ans=step[t];
				return 1;
			}
		}
	}
	for(int i=1;i<=4;i++)
	{
		for(int j=1;j<=3;j++)
		{
			node tmp=a;
			swap(tmp.a[i][j],tmp.a[i][j+1]);
			long long int y=turn(tmp);
			if(mp[y]==0)//如果1,1或者0,0交换16位数并没有改变，判重可以去掉。
			{
				mp[y]=1;
				t++;
				q[t]=tmp;
				step[t]=step[h]+1;
			}
			if(y==p)//找到返回1；
			{
				ans=step[t];
				return 1;

			}
		}
	}
	return 0;//未找到返回0；
}
int main()
{
	node s,g;
	string c;
	for(int i=1;i<=4;i++)
	{
		cin>>c;
		for(int j=0;j<=3;j++)
		{
			s.a[i][j+1]=c[j]-'0';
		}
	}	
	long long int tt=turn(s);
	mp[tt]=1;
	for(int i=1;i<=4;i++)
	{
		cin>>c;
		for(int j=0;j<=3;j++)
		{
			g.a[i][j+1]=c[j]-'0';
		}
	}	
	p=turn(g);
	if(p==tt)
	{
		cout<<0<<endl;
		return 0;
	}
	q[t]=s;
	while(h<=t)
	{
		h++;
		if(change(h)==1)
		{
			cout<<ans<<endl;
			return 0;//输出...;
		}
	}
	return 0;
}
