//2018.9.5 by ljz
#include<bits/stdc++.h>
using namespace std;
#define res register int
#define LL long long
#define inf 0x3f3f3f3f
#define eps 1e-15
inline int read(){
    res s=0;
    bool w=0;
    char ch=getchar();
    while(ch<'0'||ch>'9'){if(ch=='-')w=1;ch=getchar();}
    while(ch>='0'&&ch<='9')s=s*10+ch-'0',ch=getchar();
    return w?-s:s;
}
inline void _swap(res &x,res &y){
    x^=y^=x^=y;
}
inline int _abs(const res &x){
    return x>0?x:-x;
}
inline int _max(const res &x,const res &y){
    return x>y?x:y;
}
inline int _min(const res &x,const res &y){
    return x<y?x:y;
}
const int N=20;
namespace MAIN{
    struct P{
        int x,y;
        P() {}
        P(res x,res y):x(x),y(y) {}
    }c[N],d[N];
    int num;
    char a[N][N],b[N][N];
    int e[N][N];
    inline int get(const P &a,const P &b){
        return _abs(a.x-b.x)+_abs(a.y-b.y);
    }
    int ans;
    bool vis[N];
    void dfs(res x,res w){
        if(w>=ans)return;
        if(x==num+1){ans=_min(w,ans);return;}
        for(res i=1;i<=num;i++)
            if(!vis[i])
                vis[i]=1,dfs(x+1,w+e[x][i]),vis[i]=0;
    }
    inline void MAIN(){
        ans=inf;
        for(res i=1;i<=4;i++){
            scanf("%s",a[i]+1);
            for(res j=1;j<=4;j++)
                if(a[i][j]=='1')d[++num]=P(i,j);
        }
        num=0;
        for(res i=1;i<=4;i++){
            scanf("%s",b[i]+1);
            for(res j=1;j<=4;j++)
                if(b[i][j]=='1')c[++num]=P(i,j);
        }
        for(res i=1;i<=num;i++)
            for(res j=1;j<=num;j++)
                e[i][j]=get(c[i],d[j]);
        dfs(1,0);
        printf("%d\n",ans);
    }
}
int main(){
    MAIN::MAIN();
    return 0;
}
