							if(f[l][k][s1]&&f[k+1][r][s2]&&can[s][s1][s2])
							{
								f[l][r][s]=1;
							}
						}
					}
				}
			}
		}
	}
	int ans=0;
	if(f[0][length-1][1]) {ans=true;printf("W");}
