		fl=1;
	}

	if(dp[0][len-1][3])
	{
		cout<<'N';
		fl=1;
	}

	if(dp[0][len-1][4])
	{
		cout<<'G';
		fl=1;
	}
	//无解输出
	if(fl==0)
	{
