I lca(ll x,ll y){D(i,n-1,0)if((x>>i)^(y>>i))return n-i-1;while(1)puts("???");return 0;}
#define ls (o<<1)
#define rs (o<<1|1)
IV dfs(ll o,ll dep,ll l,ll r){
	if(dep==n){
		dp[o][0]=C[0][o-S];dp[o][1]=C[1][o-S];
