int lowbit(int x) {
	return x & (-x);
}

void update(int x, int d) {
	while (x <= 5 * n) {
		upd[x] = max(upd[x], d);
		x += lowbit(x);
	}
}

int query(int x) {
	int res = 0;
	while (x) {
		res = max(res, upd[x]);
		x -= lowbit(x);
	}
	return res;
}

