	int ans = 0;
	for(int i = 1; i <= n; ++i)
		for(int j = pos[a[i]][0]; j >= 1; --j) {
			dp[i][j] = query(1, 1, n, 1, pos[a[i]][j] - 1) + 1;
			modify(1, 1, n, pos[a[i]][j], dp[i][j]);
			ans = max(ans, dp[i][j]);
		}
	printf("%d\n", ans);
	return 0;
}
