## 代码

```cpp
#include <iostream>
#include <cstdio>
#include <cstring>

using namespace std;

const int maxn=100000+5;
const int maxm=200000+5;

int cnt,n,m,u,v,w;

int first[maxn],outdeg[maxn];

double ans;

struct Edge
{
	int to,dis,nxt;
}a[maxm];

inline int read()
{
	int p=0,f=1;char c=getchar();
	while(c<'0'||c>'9'){if(c=='-') f=-1;c=getchar();}
	while(c>='0'&&c<='9'){p=(p<<1)+(p<<3)+(c^48),c=getchar();}
	return p*f;
}

inline void addedge(int from,int to,int dis)
{
	a[++cnt]=(Edge){to,dis,first[from]};
	first[from]=cnt;
}

inline void dfs(int u,double p,int sum)
{
	if(u==n)
	{
		ans=(double)ans+p*sum;
		return;
	}
	for(int i=first[u];i;i=a[i].nxt)
	{
		int v=a[i].to;
		dfs(v,(double)1/outdeg[u]*p,sum+a[i].dis);
	}
}

int main()
{
	n=read(),m=read();
	for(int i=1;i<=m;i++)
	{
		u=read(),v=read(),w=read();
		outdeg[u]++;
		addedge(u,v,w);
	}
	dfs(1,1,0);
	printf("%.2lf",ans);
	return 0;
}
```

