#define mod 10000007
using namespace std;

long long ans=1,n,k,cnt,dp[110][110];

long long kasumi(long long a,long long b)
{
    long long tmp=1;
    while(b)
    {
        if(b&1) tmp=(tmp%mod)*a%mod;
        a=(a%mod)*a%mod;
        b>>=1;
    }
    return tmp%mod;
}

int main()
{
    scanf("%lld",&n);
    for(int i=1; i<=60; i++)
    {
        dp[i][1]=1;
    }
    for(int i=1; i<=60; i++)
    {
        dp[i][i]=1;
    }
    for(int i=2; i<=60; i++)
    {
        for(int j=2; j<i; j++)
        {
            dp[i][j]=dp[i-1][j-1]+dp[i-1][j];
        }
    }
    for(k=1; k<=n; k<<=1)
    {
        n-=k;
        cnt++;
        for(int j=1; j<=cnt; j++)
        {
            ans=(ans%mod)*kasumi(j,dp[cnt][j])%mod;
            ans%=mod;
        }
    }
    cnt=0;
    for(int i=60;i>=0;i--)
    {
        if((1ll<<i)<=n)
        {
            for(int j=1;j<=i+1;j++)
            {
                ans=(ans%mod)*kasumi(j+cnt,dp[i+1][j]);
                ans%=mod;
            }
            cnt++;
            n-=(1ll<<i);
        }
    }
    printf("%lld\n",ans);
}
