    }
    for(int i=k;i>0;i--)
        if(al[i]){
            long long anss=1;
            for(int j=1;j<=i;j++){
            	anss=anss*power(j+m,qaq[i-1][j])%mod;
            }
            ans=ans*anss%mod;
            m++;
        }
    printf("%lld\n",ans);
    return 0;
}
