{
	mu[1] = 1;
	for(int i = 2;i <= n;i++)
	{
		if(!vis[i])
			mu[prime[++tot] = i] = -1;
		for(int j = 1;j <= tot && i * prime[j] <= n;j++)
		{
			vis[i * prime[j]] = 1;
			if(i % prime[j] == 0)
			{
				mu[i * prime[j]] = 0;
