		a[i].p = read_();
		x = read_();
		add_(x,i);
	}
	double l = 0,mid;
	while(r-l>eps) {
		mid = (l+r) / 2;
		if(work_(mid)) l = mid;
		else r = mid;
	} 
	printf("%.3lf",l);
}

