#define ll long long
#define R register
#define IL inline
#define Rf(a,b,c) for(R int (a)=(b);(a)<=(c);++(a))
#define Tf(a,b,c) for(R int (a)=(b);(a)>=(c);--(a))
#define MP make_pair
#define PA pair<int,int>
#define MES(a,b) memset((a),(b),sizeof((a)))
#define MEC(a,b) memset((a),(b),sizeof((b)))
#define D long double

using namespace std;

const int N=105;
const D eps=1e-7;

int n,m,link[N],pd;
D lx[N],ly[N],w[N][N];
bool S[N],T[N];

IL int read() {
	int x=0,f=1;char ch=getchar();
	while(ch>'9'||ch<'0'){if(ch=='-')f=-1;ch=getchar();}
	while(ch>='0'&&ch<='9'){x*=10;x+=(ch-'0');ch=getchar();}
	return x*f;
}
IL void write(int x) {
    if(x<0) putchar('-'),x=-x;
    if(x>9) write(x/10);
    putchar(x%10+'0');
}

IL bool cmp(D x, D y) {
	if(x+eps>=y&&x-eps<=y) return true;
	else return false;
}

bool dfs(int x) {
	S[x]=true;
	Rf(i,1,n) if(cmp(lx[x]+ly[i],w[x][i])&&!T[i]) {
		T[i]=true;
		if(!link[i]||dfs(link[i])) {
			link[i]=x;
			return true;
		}
	}
	return false;
}

IL void update() {
	R D a=1<<30;
	Rf(i,1,n) if(S[i]) 
		Rf(j,1,n) if(!T[j]) 
			a=min(a,lx[i]+ly[j]-w[i][j]); 
	Rf(i,1,n) {
		if(S[i]) lx[i]-=a;
		if(T[i]) ly[i]+=a;
	}
}

IL void KM() {
	Rf(i,1,n) {
		link[i]=0;
		lx[i]=ly[i]=-(1<<30);
		Rf(j,1,n) lx[i]=max(lx[i],w[i][j]);
	}
	Rf(i,1,n) while(true) {
		Rf(j,1,n) S[j]=T[j]=false;
		if(dfs(i)) break;
		else update();
	}
} 


signed main()
{
	n=read();
	Rf(i,1,n) {
		R int pd=0;
		Rf(j,1,n) {
			R D x;scanf("%Lf",&x);x/=100;
			if(!cmp(x,0)) pd=1;
			w[i][j]=log(x);	
		}
		if(!pd) {
			puts("0.000000");
			return 0;
		}
	}
	KM();
	D sum=0;
	Rf(i,1,n) sum+=lx[i]+ly[i];
	printf("%Lf",exp(sum)*100);

	return 0;
}
