			rsumy[i][j]=(rsumy[i][j+1]*base%p+a[i][j])%p;
		}
	}
	for(int j=3;j<m;j+=2)ans=max(ans,manacher(1,j));
	for(int i=1;i<n;i+=2)ans=max(ans,manacher(i,1));
	printf("%d\n",ans==1?-1:ans);
	return 0;
}

