void prepare() {
	for( register int i = n - 1; i >= 0; --i ) 
		for( register int j = m - 1; j >= 0; --j ) 
			for( int k = 0; k < 2; ++k ) {
				int r = i + dr[k] , c = j + dc[k]; 
				if( r < 0 || r >= n || c < 0 || c >= m ) ex[i][j][k] = 0;
				else ex[i][j][k] = ex[r][c][k] << 1;
				ex[i][j][k] |= mat[i][j] == '1'; 
			}
	for( register int i = 0; i < n; ++i )
		for( register int j = 0; j < m; ++j )
