	if(!m || n == m) return 1;
	if(n < m) return 0;
	return Lucas(n / p, m / p) * c[n % p][m % p] % p;
}
signed main() {
	c[0][0] = f[0][0] = 1;
	rep(i, 1, p) {
		f[i][0] = c[i][0] = c[i][i] = 1;
		rep(j, 1, i - 1) c[i][j] = (c[i - 1][j] + c[i - 1][j - 1]) % p;
		rep(j, 1, i) f[i][j] = (f[i][j - 1] + c[i][j]) % p;
