#include <cstdio>
using namespace std;

const int p = 2333;

int fac[3000], inv[3000];
int f[3000][3000];

int qpow(int x, int y)
{
	int res = 1;
	for (x %= p; y > 0; x = x * x % p, y >>= 1) if (y & 1) res = res * x % p;
	return res;
}

int c(long long n, long long m)
{
	if (n < m || m < 0) return 0;
	if (n < p && m < p) return fac[n] * inv[m] % p * inv[n - m] % p;
	return c(n / p, m / p) * c(n % p, m % p) % p;
}

int work(long long n, long long k)
{
	if (n < p && k < p) return f[n][k];
	return (c(n / p, k / p) * work(n % p, k % p) + work(n % p, p - 1) * work(n / p, k / p - 1)) % p;
}

int main()
{
	fac[0] = 1;
	for (int i = 1; i < p; i++)
		fac[i] = fac[i - 1] * i % p;
	inv[p - 1] = qpow(fac[p - 1], p - 2);
	for (int i = p - 1; i >= 1; i--)
		inv[i - 1] = inv[i] * i % p;
	for (int i = 0; i < p; i++)
	{
		f[i][0] = c(i, 0);
		for (int j = 1; j < p; j++)
			f[i][j] = (f[i][j - 1] + c(i, j)) % p;
	}
	int t; scanf("%d", &t);
	while (t --> 0)
	{
		long long x, y;
