        i++;
        p=ksc(p,abs(t1-t2),x);
        if(p==0)
        {
            int t=gcd(abs(t1-t2),x);
            if(t!=1 && t!=x) 
                Pollard_Rho(t),Pollard_Rho(x/t);
            return;
