Int qpow(Int a,Int b,Int mod){
    Int A=1;
    while(b){
        if(b&1)A=(__int128)A*a%mod;
        a=(__int128)a*a%mod;
        b>>=1;
    }
    return A;
}
Int gcd(Int a,Int b){
    if(!b)return a;
    else return gcd(b,a%b);
}
void exgcd(Int a,Int b,Int &x,Int &y){
    if(!b)x=1,y=0;
    else{
        Int xx,yy;
        exgcd(b,a%b,xx,yy);
        x=yy,y=xx-a/b*yy;
    }
}
Int inv(Int a,Int b){
    Int x,y;
    exgcd(a,b,x,y);
    return (x%b+b)%b;
}

bool Miller_Rabin(Int p){
    if(p<2)return 0;
    if(p==2)return 1;
    Int d=p-1,r=0;
    while((d&1)==0)++r,d>>=1;
    for(Int k=1;k<=10;++k){
        Int a=rand()%(p-2)+2;
        Int x=qpow(a,d,p);
        if(x==1||x==p-1)continue;
        for(int i=0;i<r-1;++i){
            x=(__int128)x*x%p;
            if(x==p-1)break;
        }
        if(x!=p-1)return 0;
    }
    return 1;
}

Int Pollard_Rho(Int x){
    Int s=0,t=0;
    Int c=rand()%(x-1)+1;
    Int val=1;
    for(int goal=1;;goal*=2,s=t,val=1){
        for(int step=1;step<=goal;++step){
            t=((__int128)t*t+c)%x;
            val=(__int128)val*std::abs(t-s)%x;
            if(step%127==0){
                Int d=gcd(val,x);
                if(d>1)return d;
            }
        }
        Int d=gcd(val,x);
        if(d>1)return d;
    }
}

Int ans;
void fac(Int x){
    if(x<=ans||x<2)return;
    if(Miller_Rabin(x)){
        ans=max(ans,x);
        return;
    }
    Int p=x;
    while(p>=x)p=Pollard_Rho(x);
    while(x%p==0)x/=p;
    fac(x),fac(p);
}
