	for(qwq int i = 1 ; i <= n ; i++) lg[i] = lg[i - 1] + log(i) ; 
	for(qwq int i = 2 ; i <= n ; i++) inv[i] = ( - kmod / i + kmod) * inv[kmod % i] % kmod ;
