#include <iostream>
#include <cstring>
#include <algorithm>
#include <cstdio>

using namespace std;

int n, res;

int gcd(int a, int b)
{
	return !b ? a : gcd(b, a % b);
}

int main()
{
	cin >> n;

	for (int i = 1; i <= n / i; i ++ )
		if (n % i == 0)
		{

			int d = i;
			for (int r = 1; r <= (d + 1) / 2; r ++ )
			{
				int c = d + 1 - r;
				if (gcd(r, c) == 1)
					res ++ ;
			}
			if (i * i == n) continue;
			d = n / i;
			for (int r = 1; r <= (d + 1) / 2; r ++ )
			{
				int c = d + 1 - r;
				if (gcd(r, c) == 1)
					res ++ ;
			}
		}

	cout << res << endl;

	return 0;
}
```

