    int sum = 0;
	for(int i = 0 ; i < N ; i++){
		if(r >= S[i])
			sum += (min(r , E[i]) - S[i]) / D[i] + 1;
		if(l >= S[i])
			sum -= (min(l , E[i]) - S[i]) / D[i] + 1;
	}
	return !(sum & 1);
}
int main(){
	for(int T = read() ; T ; T--){
		N = read();
