		for(int i = 0 ; i < N ; i++){
			S[i] = read();
			E[i] = read();
			D[i] = read();
			sum += (E[i] - S[i]) / D[i] + 1;
			r = max(r , E[i]);
		}
		if(sum & 1){
        //如果人数总和为奇数表示一定有一个点
			long long p = 1;
			while(p)
            //倍增核心
				if(l + p < r && check(l , l + p)){
					l += p;
					p <<= 1;
				}
				else	p >>= 1;
			printf("%d " , ++l);
			//记得l要+1
            int q = 0;
			for(int i = 0 ; i < N ; i++)
				if(E[i] >= l && l >= S[i] && (l - S[i]) % D[i] == 0)
					q++;
			printf("%d\n" , q);
		}
        //否则一定没有点
		else	printf("Poor QIN Teng:(\n");
	}
	return 0;
}
