using namespace std;
typedef long long ll;
const int N=200005;
ll s[N],e[N],d[N];
int n;
ll cal(ll x)
{
	ll ans=0;
	for(int i=1;i<=n;i++)
	{
		if(s[i]<=x)
		{   
			ll sum=(min(x,e[i])-s[i])/d[i]+1;
			ans+=sum;
		}
	}
	return ans;
}
int main()
{
	int t;
	scanf("%d",&t);
	for(int i=1;i<=t;i++)
	{
		scanf("%d",&n);
		ll l=2147483647,r=0;
		for(int k=1;k<=n;k++)
		{
			scanf("%lld%lld%lld",&s[k],&e[k],&d[k]);
			r=max(r,e[k]);
			l=min(l,s[k]);
		}
		if(cal(r)%2==0) printf("Poor QIN Teng:(\n");
        else
        {
        	while(l!=r)
			{
				ll mid=(l+r)>>1;
				if(cal(mid)%2==1) r=mid;
				else l=mid+1;
			}
		  	ll num=cal(l)-cal(l-1);
		   	printf("%lld %lld\n",l,num);
        }
	}
	return 0;
}
