int n,a[100005],x[100005],y[100005],l,r;
bool check(int t){
    x[1]=y[1]=a[1];
    rep(i,2,n){
        x[i]=max(0,a[1]+a[i-1]+a[i]-y[i-1]-t);
        y[i]=min(a[i],a[1]-x[i-1]);
        if(x[i]>y[i]) return 0;
    }
    if(!x[n]) return 1;
    else return 0;
}
int main(){
    scanf("%d",&n);
