```c
#include <stdio.h>

//冒泡排序，从小到大
void BubbleSort(int nums[], int n)
{
	for (int i = 0; i < n; i++)
	{
		for (int j = 0; j < n - i - 1; j++)
		{
			if (nums[j] > nums[j + 1])
			{
				int temp = nums[j];
				nums[j] = nums[j + 1];
				nums[j + 1] = temp;
			}
		}
	}
}

int main(void)
{
	int nums[3];
	char str[4];

	scanf("%d %d %d", &nums[0], &nums[1], &nums[2]);

	BubbleSort(nums, 3);

	scanf("%s", str);

	for (int i = 0; str[i] != 0; i++)
	{
		switch (str[i])
		{
		case 'A':
			printf("%d ", nums[0]);
			break;
		case 'B':
			printf("%d ", nums[1]);
			break;
		case 'C':
			printf("%d ", nums[2]);
			break;
		}
	}
	printf("\n");

	return 0;
}
```

