代码：

```
#include<cstdio>
#include<cstring>
#include<iostream>
#include<string>
using namespace std;
const int N=4e2+10;
int n,m;
int sum[N][N];
int maxx=0;
string s;
int read(){//弱智读入 
	int t;cin>>t;
	return t;
}
int print(int x){//弱智输出 
	cout<<x<<endl; 
}
int solve(int x,int y){//长和宽分别为x和y的矩阵的周长 
	return (x+y)<<1;
}
int main(){
	n=read();m=read();
	for(int i=1;i<=n;i++){
		cin>>s;
		for(int j=0;j<m;j++)
			if(s[j]=='.')sum[i][j+1]=sum[i][j]+1;//记录前缀和 
	}
	int minn;
	for(int i=1;i<=n;i++)//枚举 
		for(int j=1;j<=m;j++){
			minn=999999999;//记录行的最小前缀 
			for(int k=i;k>=1;k--){
				minn=min(minn,sum[k][j]);
				if(!minn)break;//当然不能为0 
				maxx=max(maxx,solve(i-k+1,minn));//记录最大值 
			}
		}
	print(maxx-1);return 0;
}

```

