		cout<<"DOSTA";//但是亲测不加会WA 
		return 0;     //并且会使测评机出一些奇怪的错误 
	}                 //导致AC代码WA两个点 
	for(int i=1;i<=X;i++)tot1+=a[i];
	for(int i=X;i<=11;i++)tot2+=a[i];
