```
#include <iostream>
#include <stdio.h>
#include <cstring>
#include <map>
using namespace std;
map<long long, int> used, jiaed;
const int N = 200010;
int n, cnt;
long long zhan[N];
char str[N][15];
long long has(int a, int l, int r)
{
	long long ans = 1ll;
	for(int i = l;i <= r; ++i)
	ans = (ans * 26 + (str[a][i] - 'a') + 1);//26进制 
	return ans;
}
void add()//将每个大串扔进map 
{
	for(int i = 1;i <= n; ++i)
	{
		int len = strlen(str[i]);
		used[has(i, 0, len - 1)]++;
	}
}
int check(int a)
{
	int ans = -1, len = strlen(str[a]), p = 0;
	for(int l = 0;l < len; ++l)
	 for(int r = l ;r < len; ++r)//枚举子串 
	  if(used[has(a, l, r)] && !jiaed[has(a, l, r)]) 
	  {
	  	ans += used[has(a, l, r)]; jiaed[has(a, l ,r)] = 1;// 不要加重复了哟 
	  	zhan[++p] = has(a, l ,r);
	  }
	for(int i = 1;i <= p; ++i) jiaed[zhan[i]] = 0;
	return ans;
}
int main()
{
	scanf("%d",&n);
	for(int i = 1;i <= n; ++i)
	scanf("%s", str[i]); 
