#include <bits/stdc++.h>
#define ll long long 
#define ls (x<<1)
#define rs (x<<1|1)
using namespace std;

const int N=2e5+10;
const int inf=0x3f3f3f3f;

int n,q,p,ans,P[N];
int mid[N<<2],m[N<<2],v[N<<2];
int dfn[N<<2];

int Q(int x,int w) {
	if(dfn[x]) return dfn[x]+max(m[x],w);
	if(m[rs]>=w) return min(v[x],Q(rs,w));
	return min(Q(ls,max(w,m[rs])),mid[x]+1+w);
}
inline void upd(int x) {
	m[x]=max(m[ls],m[rs]);
	v[x]=Q(ls,m[rs]);
}
void build(int x,int l,int r) {
	if(l==r) {
		m[x]=P[l];
		v[x]=l+P[l];
		dfn[x]=l;
		return;
	}
	mid[x]=(l+r)>>1;
	build(ls,l,mid[x]);
	build(rs,mid[x]+1,r);
	upd(x);
}
void modify(int x,int l,int r,int p) {
	if(l==r) {
		m[x]=P[l];
		v[x]=l+P[l];
		return;
	}
	if(p<=mid[x]) modify(ls,l,mid[x],p);
	else modify(rs,mid[x]+1,r,p);
	upd(x);
}

int main() {
	scanf("%d%d%d",&n,&q,&p);
	for(int i=1; i<=n; ++i) scanf("%d",P+i),P[i+n]=P[i];
	for(int i=1; i<=n*2; ++i) P[i]-=i;
	build(1,1,n*2);
	printf("%d\n",ans=n-1+v[1]);
	for(int x,y; q--; ) {
		scanf("%d%d",&x,&y);
		if(p) x^=ans,y^=ans;
		P[x]=y-x; 
		P[x+n]=y-x-n; 
		modify(1,1,n*2,x);
		modify(1,1,n*2,x+n);
		printf("%d\n",ans=n-1+v[1]);
	}
	return 0;
}
