    scanf("%d%d",&k,&n);
    while(k--){
        string s;
        cin>>s;
        a[s[0]-'a'].push_back(s);//vector的基本用法
    }
    for (int i=0;i<26;i++)sort(a[i].begin(),a[i].end());//C++对string的排序默认按字典序
    while (n--){
    	cin>>c;
    	cout<<a[c-'a'][q[c-'a']]<<endl;
    	q[c-'a']=(q[c-'a']+1)%a[c-'a'].size();//+1指向下一个，同时取模
        //经观察可发现：
        //单词：zadar zoo
        //次数：0     0
        //第一次选zadar：
        //      zadar zoo
        //      1     0
        //则第二次选zoo：
        //      zadar zoo
        //      1     1
        //第三次又选zadar：
        //      zadar zoo
        //      2     1
        //…
        //则可以发现：无论有多少次，在同一字母开头的单词中下标一直单调递增
        //所以直接+1就可以啦。
    }
}
