#include<bits/stdc++.h>
#include<iostream>
#include<cstring>
#include<cstdio>
#include<algorithm>
#include<cstdlib>
using namespace std;
typedef long long ll;

const int INF = 0x3f3f3f3f;
const int MAXN = 5e6 + 5;
const int MOD = 1e9 + 7;

int T,n,m,k,tot;
int p[MAXN],prime[MAXN],f[MAXN],g[MAXN];

namespace ModOperation{
	int add(int x,int y){
		x += y;
		if(x >= MOD)
			x -= MOD;
		return x;
	}

	int sub(int x,int y){
		x -= y;
		if(x < 0)
			x += MOD;
		return x;
	}

	int mul(int x,int y){
		return 1ll * x * y % MOD;
	}
}

using namespace ModOperation;

int power(int x,int y){
	int res = 1;
	while(y){
		if(y & 1)
			res = mul(res,x);
		x = mul(x,x);
		y >>= 1;
	}
	return res;
}

void Init(int n){
	f[1] = 1;
	for(int i = 2;i <= n;i++){
		if(!prime[i]){
			p[++tot] = i;
			g[i] = power(i,k);
			f[i] = sub(g[i],1);
		}
		for(int j = 1;j <= tot;j++){
			if(i * p[j] > n)
				break;
			prime[i * p[j]] = 1;
			if(i % p[j] == 0){
				f[i * p[j]] = mul(f[i],g[p[j]]);
				break;
			}
			f[i * p[j]] = mul(f[i],f[p[j]]);
		}
	}
	for(int i = 1;i <= n;i++)
		f[i] = add(f[i],f[i - 1]);
}

int main(){
	//freopen(".in","r",stdin);
	//freopen(".out","w",stdout);
	scanf("%d%d",&T,&k);
	Init(5000000);
	while(T--){
		scanf("%d%d",&n,&m);
		if(n > m)
			swap(n,m);
		int ans = 0;
		for(int l = 1,r;l <= n;l = r + 1){
			r = min(n / (n / l),m / (m / l));
			ans = add(ans,mul(sub(f[r],f[l - 1]),mul(n / l,m / l)));
		}
		printf("%d\n",ans);
	}
	return 0;
}


