	}
	if(!((t & 1) ^ opt)) { // Alice
		for(int i = S; i * i <= prod; ++i)
			if(prod % i == 0 && prod / i >= S && !f(t - 1, i, prod / i)) ++cnt;
		return ok[t][mp(a, b)] = (cnt == 1);
	} else { // Bob
		for(int i = S; i <= (sum >> 1); ++i)
			if(sum - i >= S && !f(t - 1, i, sum - i)) ++cnt;
		return ok[t][mp(a, b)] = (cnt == 1);
	}
} 

bool valid(int a, int b) { return f(T, a, b) && (!f(T - 2, a, b)) && (!f(T - 1, a, b)); }

signed main () {
