			if(!valid(a, b)) continue;
			if((T & 1) ^ opt) { // Alice
				for(int i = S; i * i <= prod; ++i)
					if(prod % i == 0 && prod / i >= S && valid(i, prod / i)) ++cnt;
				if(cnt != 1) continue;
			} else {
				for(int i = S; i <= (sum >> 1); ++i)
					if(sum - i >= S && valid(i, sum - i)) ++cnt;
				if(cnt != 1) continue;
			}
			printf("%d %d\n", a, b);
			return 0;
		}
	}
