            for(auto g:X[a])c+=Yi[g.first+g.second];
            if(c>=2)User.push_back(a);
            else Xi[a]=0;
        }
        Ag=User;
        User.clear();
        for(auto b:Bg){
            uint c=0;
            for(auto g:Y[b])c+=Xi[g.first*g.second];
            if(c>=2)User.push_back(b);
