	Try();
	for(register int i=1; i<(1<<n); i++) {
		register int siz=GetSiz(i);
		for(register int j=1; j<=n; j++) {
			if(!((i>>(j-1))&1))continue;
			if(!(i>>(j-1)))break;
			if(siz==1) {
				f[i][j]=1;
				break;
			}
			for(register int k=1; k<=n; k++) {
				if((!((i>>(k-1))&1))||k==j)continue;
				if(Check(i,k,j))f[i][j]+=f[i-(1<<(j-1))][k];
				if(f[i][j]>mod)f[i][j]-=mod;
			}
			if(siz>=4)ans+=f[i][j];
			if(ans>mod)ans-=mod;
		}
	}
	printf("%d\n",ans);
	return 0;
}
